import React, { Component } from 'react'
import Header from '../Header/header.js';
import {FormControl,Card,Form,Button,Image,Container,Row,Col} from 'react-bootstrap';
import StarRatingComponent from 'react-star-rating-component';
import './userProfile.css'

class UserProfile extends Component{
    constructor() {
        super();
     
        this.state = {
          rating: 4,
          editOption:true,
          name:"Arya Stark",
          dob:'1993-03-05',
          phone:9021110000,
          email:'arya93@gmail.com',
          edu:'Bachelor\'s in CS',
          emp:'Software Engineer',
          hobbies:'Archery',
          intro:'Some quick example text to build on the card title and make up the bulk of the card\'s content'
        }
        
    }

    onStarClick(nextValue, prevValue, name) {
        console.log();
        
        this.setState({rating: nextValue});
    }

    assignValue=(event)=>{
        console.log(event.target.value);
        this.setState({
            [event.target.id]:event.target.value
        })
    }

    editEnable=()=>{
        this.setState({
            editOption:false
        })
    }

    validate=()=>{
        var phoneRe = /^([0-9]{10})$/
        if(this.state.name===''){
            alert('Please enter name details')
            return false
        }
        else if(this.state.data===''){
            alert('Please enter Date of Birth')
            return false
        }
        else if(this.state.phone==='' || !phoneRe.test(this.state.phone)){
            alert('Please enter a valid Phone number with 10 digits')
            return false
        }
        var re = /^(?:[a-z0-9!#$%&amp;'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&amp;'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])$/;
        if(this.state.email==='' || !re.test(this.state.email)){
            alert('Please enter a proper email address')
        }
        else{
            alert('Succesfully saved the changes')
            return true
        }

    }

    submitForm=()=>{
        var allowSubmission = this.validate()
        if(allowSubmission){
            this.setState({
            editOption:true
            })
        }
    }

    previewFile(){
        var preview = document.getElementById('img1') //selects the query named img
        var file    = document.querySelector('input[type=file]').files[0]; //sames as here
        var reader  = new FileReader();
 
        reader.onloadend = function () {
            preview.src = reader.result;
        }
 
        if (file) {
            reader.readAsDataURL(file); //reads the data as a URL
        } else {
            preview.src = "";
        }
   }

