import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import { InputGroup,Image, FormControl, Form,Checkbox, Button } from 'react-bootstrap';
import './login.css'
import { createBrowserHistory } from 'history';
const history = createBrowserHistory();

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
};

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));


class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            value: 0,
            check: true,
            email_log:'',
            pass_log:'',
            uname:'',
            email_reg:'',
            phone:'',
            new_pass:'',
            confirm_pass:''
        };
    }
    handleChange=(event, newValue)=>{
        this.setState({value:newValue});
    }

    checkBoxChange=(event)=>{
        console.log(this.state.check);
        this.setState({
            check: !(this.state.check)
        })
    }

    validateForm=(id,value)=>{
        var emailRe = /^(?:[a-z0-9!#$%&amp;'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&amp;'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])$/;
        if(id==='register'){
            var nameRe = /^[a-zA-Z]+$/
            var phoneRe = /^([0-9]{10})$/
            var passRe = /^([0-9][a-zA-Z]){4,}$/
            
            if(this.state.uname==='' || !nameRe.test(this.state.uname)){
                alert('Please enter name. Only Alphabet allowed')
                return false
            }
            else if(this.state.email_reg==='' || !emailRe.test(this.state.email_reg)){
                alert('Please enter valid email address')
                return false
            }
            else if(this.state.phone==='' || !phoneRe.test(this.state.phone)){
                alert('Please enter valid phone number that has 10 digits')
                return false
            }
            else if(this.state.new_pass==='' || !passRe.test(this.state.new_pass)){
                alert('Please enter a password')
                return false
            }
            else if(this.state.confirm_pass===''){
                alert('Please confirm password')
                return false
            }
            else if(this.state.new_pass !== this.state.confirm_pass){
                alert('Your passwords do not match, please type again')
                return false
            }
            else{
                return true
            }
        }
        else{
            if(this.state.email_log==='' || !emailRe.test(this.state.email_log)){
                alert('Please enter valid email address')
                return false
            }
            else if(this.state.pass_log===''){
                alert('Please enter the password')
                return false
            }
            else{
                return true
            }
        }        
    }

    submitForm=(event)=>{
        console.log(event.target.value);
        var passCheck = this.validateForm(event.target.id,event.target.value)
        if(passCheck && event.target.id==='login'){
            history.push('/home')
            history.go()
        }
        console.log(passCheck);
        
    }

    assignValue=(event)=>{
        console.log(event.target.id);
        this.setState({
            [event.target.id]:event.target.value
        })
    }

    render(){
        
        return(
        <div>
            <div className="justify-center" style={{marginTop: '23px',marginBottom: '21px'}}><Image src="/images/logoWithText.png" className="img-size"/></div>
            <div className="container_login">                
                <AppBar position="static" >
                    <Tabs
                        value={this.state.value}
                        onChange={this.handleChange}
                        indicatorColor="secondary"
                        textColor="secondary"
                        variant="fullWidth"
                    >
                        <Tab label="My Account" />
                        <Tab label="Sign Up" />
                    </Tabs>
                </AppBar> 
                {this.state.value === 0 && <TabContainer>
                    <InputGroup className="mb-3" style={{marginTop:'25px',marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/user.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.email_log}
                        value={this.state.email_log}
                        onChange={this.assignValue.bind(this)}
                        placeholder="Email"
                        id="email_log"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <InputGroup className="mb-3" style={{marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/key.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.pass_log}
                        onChange={this.assignValue.bind(this)}
                        type="password"
                        placeholder="Password"
                        id="pass_log"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <Form.Check type="checkbox" className="small-grey-text" checked={this.state.check} onChange={this.checkBoxChange} label="Remember Me" />
                    <div className="justify-center button-placement">
                        <Button variant="primary" type="submit" id="login" onClick={this.submitForm}>
                            LOGIN
                        </Button>
                    </div>
                    <div className="justify-center forgotpass-placement">
                        <a href="" className="small-link-text">Forgot Password?</a>
                    </div>
                </TabContainer>}
                {this.state.value === 1 && <TabContainer>
                    <InputGroup className="mb-3" style={{marginTop:'25px',marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/user.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.uname}
                        onChange={this.assignValue.bind(this)}
                        placeholder="Name"
                        id="uname"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <InputGroup className="mb-3" style={{marginTop:'25px',marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/email.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.email_reg}
                        onChange={this.assignValue.bind(this)}
                        placeholder="E-mail"
                        id="email_reg"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <InputGroup className="mb-3" style={{marginTop:'25px',marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/phone.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.phone}
                        onChange={this.assignValue.bind(this)}
                        type="number"
                        placeholder="Phone"
                        id="phone"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <InputGroup className="mb-3" style={{marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/lock.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.new_pass}
                        onChange={this.assignValue.bind(this)}
                        type="password"
                        placeholder="Enter Password"
                        id="new_pass"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <InputGroup className="mb-3" style={{marginLeft:'16%',width:'70%'}}>
                        <InputGroup.Prepend>
                        <InputGroup.Text id="basic-addon1"><Image src="/images/key.png" className="field-img"/></InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                        defaultValue={this.state.confirm_pass}
                        onChange={this.assignValue.bind(this)}
                        type="password"
                        placeholder="Confirm Password"
                        id="confirm_pass"
                        aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                    <div className="justify-center button-placement">
                        <Button variant="primary" id="register" type="submit" onClick={this.submitForm}>
                            Sign me up!
                        </Button>
                    </div>
                    
                </TabContainer>}              
            </div>
        </div> 
         )
    }
}

export default Login;
